@use "../abstracts/index" as *;

.header {
  .logo {
    display: flex;
    justify-content: center;
    align-items: center;

    cursor: pointer;
    &__img {
      @include border-active-black;

      max-height: 5rem;
    }

    &__text {
      text-align: center;
      box-sizing: border-box;

      @include underline-hover-black;
      @include border-active-black;
    }
  }
  .nav {
    flex: 1;

    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: flex-end;
    gap: clamp(0.5rem, 1rem + 3vw, 6rem);
    margin-right: 1rem;
    &__item {
      list-style-type: none;
      @include underline-hover-black;
      @include border-active-black;

      & a,
      & a:link,
      & a:visited {
        text-decoration: none;
        color: inherit;
      }
    }

    &__item:not(:last-child) {
    }

    @media only screen and (max-width: 1000px) {
      justify-content: center;
      margin: 0;
    }
  }
}

